mapper.select:
SELECT /* movie_SQL.list */ ROW_NUMBER() OVER (ORDER BY A.INS_DT DESC) RNUM ,A.MOVIE_CD ,A.MOVIE_NM ,A.GENRE_CD ,A.PRODUCTION ,A.INS_DT ,A.DEL_YN FROM TCM_MOVIE A WHERE 1=1
AND A.MOVIE_CD = #{s_movie_cd}
AND A.MOVIE_NM LIKE '%'||#{s_movie_nm}||'%'

mapper.update:
	/* movie_SQL.updateMovieImage */
	UPDATE TCM_MOVIE 
	   SET MOVIE_NM = #{i_movie_nm}
     WHERE MOVIE_CD = #{i_movie_cd}

	/* movie_SQL.deleteMovieImage */
	UPDATE TCM_MOVIE 
	   SET DEL_YN = 'Y'
     WHERE MOVIE_CD = #{i_movie_cd}
