mapper.select:
SELECT /* pp_SQL.channel_list */ A.CH_CD ,A.CH_NM ,A.CH_KIND_CD ,A.BROAD_KIND_CD ,A.CH_NO ,A.HD_YN ,A.INS_DT FROM TCM_CHANNEL A WHERE 1=1 AND A.DEL_YN = 'N'
AND A.PP_CD = #{s_corpid}
AND A.SCP_CD = #{s_corpid}

mapper.insert:
/* pp_SQL.insert */
SELECT SEQ_TB_BOARD_IDX.NEXTVAL FROM DUAL
INSERT INTO TB_BOARD ( IDX, TITLE, CONTENTS, HIT_CNT, DEL_GB, CREA_DTM, CREA_ID ) VALUES ( #{IDX}, #{TITLE}, #{CONTENTS}, 0, 'N', SYSDATE, 'Admin' )

mapper.update:
	/* pp_SQL.update */
	UPDATE TCM_MOVIE 
	   SET MOVIE_NM = #{movie_nm_detail}
     WHERE MOVIE_CD = #{movie_cd_detail}

	/* pp_SQL.delete */
	UPDATE TCM_MOVIE 
	   SET DEL_YN = 'Y'
     WHERE MOVIE_CD = #{movie_cd_detail}
